package week1.day1;

public class LearnVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String brandName = "Honda";
		System.out.println(brandName);
		
		//int for whole numbers  and double for decimal
		short noOfWheels = 500;
		long carPrice = 234324453L;
		
		System.out.println(noOfWheels);
		System.out.println(carPrice);
//decimal float and double
		float fuelCapacity = 45.45f;
		double kmsRan = 5744554.2323523652;
		
		System.out.println(fuelCapacity);
		System.out.println(kmsRan);
		
		char fuelType = 'p';
		System.out.println(fuelType);
		
		boolean preOwned = false ;//true or false
		
	}

}
